import axios from "axios";
import {LOCAL_HOST_ACCOUNT_MANAGER_UPDATE_POINTS} from "../constants/back";

const addPointsToAccount = async (score, account) => {
    try {
        const response = await axios.post("/api/account-manager/update-points", account, {
            params: {
                score: score // Passe `score` dans les paramètres de la requête
            }
        });
        return response.data;
    } catch (error) {
        console.error("Erreur lors de l'ajout des points : ", error);
        return null;
    }
};
