import axios from "axios";
import {GET_DELIVERY, LOCAL_HOST_DELIVERY, UPDATE_DELIVERY} from "../constants/back";
import {useState} from "react";

export const getDeliveryById = async (deliveryId) => {
    try {
        const response = await axios.get(LOCAL_HOST_DELIVERY + deliveryId);
        return response.data;
    } catch (error) {
        console.error("Erreur lors de la récupération des informations de livraison : ", error);
        return null;
    }
};

//Update delivery method :
export const updateDeliveryMethod = async (delivery) => {
    axios.post(UPDATE_DELIVERY, delivery).then((response) => {
        setDeliveryData();
    }).catch(error => {
        alert("Error Ocurred in updateDeliveryMethod:" + error);
    });
}

export default function Delivery() {
    const [deliverys, setDeliverys] = useState([]);
    const [newDelivery, setNewDelivery] = useState({
        account: '',
        entrepot: '',
        transportation: '',
        deliveryMethod: ''
    });

    const setDeliveryData = async () => {
        axios.get(GET_DELIVERY).then((response) => {
            setDeliverys(response.data);
        }).catch(error => {
            alert("Error Ocurred while loading data:" + error);
        });
    }

}
