import axios from "axios";
import {GET_CUSTOMERS, LOCAL_HOST_ACCOUNT} from "../constants/back";
import React, {useEffect, useState} from "react";

export const getAccountById = async (accountId) => {
    try {
        const response = await axios.get(LOCAL_HOST_ACCOUNT + accountId);
        return response.data;
    } catch (error) {
        console.error("Erreur lors de la récupération du compte : ", error);
        return null;
    }
};

export default function Account() {
    const [accounts, setAccounts] = useState([]);

    const setAccountData = async () => {
        axios.get(GET_ACCOUNT).then((response) => {
            setCustomers(response.data);
        }).catch(error => {
            alert("Error occurred while loading customers: " + error);
        });
    };

    useEffect(() => {
        setCustomerData();
    }, []);

    if (customers.length === 0) return <div className="container text-center">No customers</div>;
    console.log(customers);

    return (
        <div className="container text-center">
            <h4 className="mx-2">Liste des clients</h4>
            <div className="row">
                <table className="table table-sm table-bordered table-hover">
                    <thead>
                    <tr>
                        <th scope="col">Id</th>
                        <th scope="col">Nom</th>
                        <th scope="col">Prénom</th>
                        <th scope="col">Adresse</th>
                    </tr>
                    </thead>
                    <tbody className="table-group-divider">
                    {
                        customers.map((customer, index) => (
                            <tr key={index}>
                                <th scope="row">{customer.customerId}</th>
                                <td>{customer.lastname}</td>
                                <td>{customer.firstname}</td>
                                <td>{customer.address}</td>
                            </tr>
                        ))
                    }
                    </tbody>
                </table>
            </div>
        </div>
    );
};
