import React, { useEffect, useState } from 'react';
import axios from "axios";
import '../styles/Sample.css';
import { GET_SAMPLES, LOCAL_HOST_SAMPLE, UPDATE_SAMPLES, ADD_SAMPLE } from "../constants/back"; // Ajoute ADD_SAMPLE pour l'URL d'ajout.

export default function Sample() {
  const [samples, setSamples] = useState([]);
  const [isAdding, setIsAdding] = useState(false);  // État pour gérer l'affichage du formulaire d'ajout
  const [newSample, setNewSample] = useState({
    dateSample: '',
    stringSample: '',
    floatSample: '',
    sampleType: 'SAMPLE_TYPE1'  // Valeur par défaut pour le type d'échantillon
  });

  // Fonction pour récupérer les échantillons existants
  const setSampleData = async () => {
    axios.get(GET_SAMPLES).then((response) => {
      setSamples(response.data);
    }).catch(error => {
      alert("Error Ocurred while loading data:" + error);
    });
  }

  // Fonction pour gérer la suppression d'un échantillon
  const confirmRemoveSample = (id) => {
    if (window.confirm('Are you sure?')) {
      removeSample(id);
    }
  };

  // Fonction pour supprimer un échantillon
  const removeSample = async (id) => {
    axios.delete(LOCAL_HOST_SAMPLE + id).then((response) => {
      setSampleData();
    }).catch(error => {
      alert("Error Ocurred in removeSample:" + error);
    });
  }

  // Fonction pour mettre à jour la date d'un échantillon
  const handleChangeDate = (idSample, newDate) => {
    setSamples(prevData => prevData.map(
      row => row.idSample === idSample ? { ...row, dateSample: newDate } : row
    ));
  }

  // Fonction pour envoyer la mise à jour à l'API
  const updateDate = async (sample) => {
    axios.post(UPDATE_SAMPLES, sample).then((response) => {
      setSampleData();
    }).catch(error => {
      alert("Error Ocurred in updateDate:" + error);
    });
  }

  // Fonction pour gérer les changements dans le formulaire d'ajout
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewSample(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  // Fonction pour ajouter un nouvel échantillon
  const addSample = async () => {
    axios.post(ADD_SAMPLE, newSample).then(() => {
      setSampleData();  // Rafraîchir la liste des échantillons
      setIsAdding(false);  // Fermer le formulaire après l'ajout
      setNewSample({
        dateSample: '',
        stringSample: '',
        floatSample: '',
        sampleType: 'SAMPLE_TYPE1'  // Réinitialiser le formulaire
      });
    }).catch(error => {
      alert("Error Ocurred while adding sample:" + error);
    });
  };

  useEffect(() => {
    setSampleData();
  }, []);

  if (samples.length === 0) {
    return (<div className="container text-center">No samples</div>);
  }

  return (
    <div className="container text-center">
      <h4 className="mx-2">Samples List</h4>

      {/* Bouton pour afficher le formulaire d'ajout */}
      <button
        className="btn btn-outline-primary my-3"
        onClick={() => setIsAdding(true)}
      >
        Add a Sample
      </button>

      {/* Formulaire d'ajout d'échantillon */}
      {isAdding && (
        <div className="my-4">
          <h5>Add New Sample</h5>
          <div className="mb-3">
            <input
              type="date"
              className="form-control"
              name="dateSample"
              value={newSample.dateSample}
              onChange={handleChange}
            />
          </div>
          <div className="mb-3">
            <input
              type="text"
              className="form-control"
              name="stringSample"
              placeholder="String Sample"
              value={newSample.stringSample}
              onChange={handleChange}
            />
          </div>
          <div className="mb-3">
            <input
              type="number"
              className="form-control"
              name="floatSample"
              placeholder="Float Sample"
              value={newSample.floatSample}
              onChange={handleChange}
            />
          </div>
          <div className="mb-3">
            <select
              className="form-control"
              name="sampleType"
              value={newSample.sampleType}
              onChange={handleChange}
            >
              <option value="SAMPLE_TYPE1">SAMPLE_TYPE1</option>
              <option value="SAMPLE_TYPE2">SAMPLE_TYPE2</option>
            </select>
          </div>
          <button className="btn btn-outline-success" onClick={addSample}>Add Sample</button>
          <button className="btn btn-outline-secondary ml-2" onClick={() => setIsAdding(false)}>Cancel</button>
        </div>
      )}

      {/* Table des échantillons */}
      <div className="row">
        <table className="table table-sm table-bordered table-hover">
          <thead>
            <tr>
              <th scope="col">Id</th>
              <th scope="col">Date</th>
              <th scope="col">String</th>
              <th scope="col">Float</th>
              <th scope="col">Type</th>
              <th scope="col">Actions</th>
            </tr>
          </thead>
          <tbody className="table-group-divider">
            {
              samples.map((sample, index) => (
                <tr key={index}>
                  <th scope="row">{sample.idSample}</th>
                  <td>
                    <div className="input-group mb-3">
                      <input
                        type="date"
                        className="form-control"
                        value={sample.dateSample.substring(0,10)}
                        onChange={e => handleChangeDate(sample.idSample, e.target.value)}
                      />
                      <button className="btn btn-outline-primary" onClick={() => updateDate(sample)}>Save</button>
                    </div>
                  </td>
                  <td>{sample.stringSample}</td>
                  <td>{sample.floatSample}</td>
                  <td>{sample.sampleType}</td>
                  <td>
                    <button
                      type="button"
                      className="btn btn-outline-danger"
                      onClick={() => confirmRemoveSample(sample.idSample)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))
            }
          </tbody>
        </table>
      </div>
    </div>
  );
}
