import React, { useState } from 'react';
import logo from '../assets/logo.jpeg';
import '../styles/App.css';

export default function Search() {
    const [searchTerm, setSearchTerm] = useState(''); // État pour stocker les mots-clés

    const handleSearch = () => {
        // Logique pour appeler le backend avec les mots-clés
        console.log("Mots-clés recherchés : ", searchTerm);

        // Vous pouvez effectuer une requête API ici
        fetch(`http://localhost:8080/search?keywords=${searchTerm}`)
            .then(response => response.json())
            .then(data => {
                console.log("Résultats de la recherche : ", data);
            })
            .catch(error => {
                console.error("Erreur lors de la recherche : ", error);
            });
    };

    return (
        <div className="Search">
            <img src={logo} alt="Logo" className="logo" />
            <h1>Recherche</h1>
            <div className="search-bar">
                <input
                    type="text"
                    placeholder="Saisissez des mots-clés"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button onClick={handleSearch}>Rechercher</button>
            </div>
        </div>
    );
}
