import React, { useState } from 'react';
import axios from 'axios';

const DemoLivraison = () => {
    const [distance, setDistance] = useState(null);
    const [customerId, setCustomerId] = useState("");
    const [storeId, setStoreId] = useState("");

    const calculateDistance = async () => {
        try {
            const response = await axios.post('http://localhost:8080/api/calculate-distance', {
                customerId: parseInt(customerId),
                storeId: parseInt(storeId),
            });
            setDistance(response.data);
        } catch (error) {
            console.error("Erreur lors du calcul de la distance : ", error);
        }
    };

    return (
        <div className="container">
            <h2>Démo Livraison</h2>
            <div>
                <label>Client ID : </label>
                <input
                    type="number"
                    value={customerId}
                    onChange={(e) => setCustomerId(e.target.value)}
                />
            </div>
            <br/>
            <div>
                <label>Magasin ID : </label>
                <input
                    type="number"
                    value={storeId}
                    onChange={(e) => setStoreId(e.target.value)}
                />
            </div>
            <button onClick={calculateDistance}>Calculer Distance</button>
            {distance !== null && (
                <div>
                    <p><strong>Distance calculée : {distance} mètres</strong></p>
                </div>
            )}
        </div>
    );
};

export default DemoLivraison;
