import React, { useEffect, useState } from 'react';
import axios from "axios";
import '../styles/Sample.css';
import {DEL_SAMPLES, GET_SAMPLES, LOCAL_HOST_SAMPLE, UPDATE_SAMPLES} from "../constants/back";

export default function Sample() {
  const [samples, setSamples] = useState([]);

  const setSampleData = async () => {
    axios.get(GET_SAMPLES).then((response) => {
      setSamples(response.data);
    }).catch(error => {
      alert("Error Ocurred while loading data:" + error);
    });
  }

  const removeSample = async (id) => {
    axios.delete(LOCAL_HOST_SAMPLE + id).then((response) => {
      alert("Sample record " + id + " deleted!");
      setSampleData();
    }).catch(error => {
      alert("Error Ocurred in removeSample:" + error);
    });
  }
  const updateDate = async (id) => {
    axios.post(UPDATE_SAMPLES).then((response) => {
      alert("Sample record " + id + " deleted!");
      setSampleData();
    }).catch(error => {
      alert("Error Ocurred in removeSample:" + error);
    });
  }

  useEffect(() => {
    const intervalId = setInterval(setSampleData, 5000);
    return () => clearInterval(intervalId);
  }, []);

  if (samples.length === 0)
    return (<div className="container text-center" >Loading...</div>)
  return (
      <div className="container text-center">
      <h4 className=" mx-2">Samples List</h4>
      <div className="row">
        <table className="table table-sm table-bordered table-hover">
          <thead>
          <tr>
            <th scope="col">Id</th>
            <th scope="col">Date</th>
            <th scope="col">String</th>
            <th scope="col">Float</th>
            <th scope="col">Type</th>
            <th scope="col">Actions</th>
          </tr>
          </thead>
          <tbody className="table-group-divider">

          {
              samples.map( (sample, index) => (
                  <tr key={index}>
                    <th scope="row">{sample.idSample}</th>
                    <td>{sample.dateSample}</td>
                    <td>{sample.stringSample}</td>
                    <td>{sample.floatSample}</td>
                    <td>{sample.sampleType}</td>
                    <td>
                      <button type="button"
                              className="btn btn-outline-primary"
                              onClick={updateDate(sample)}
                      >Edit</button>
                      <button type="button"
                              className="btn btn-outline-danger"
                              onClick={removeSample(sample.idSample)}
                      >Delete</button>
                    </td>
                  </tr>

              ))
          }

          </tbody>
        </table>
      </div>
    </div>
  );
};
