import React, { useState } from 'react';
import axios from 'axios';

import {GET_PROCESS_ROUTES, GET_PROCESS_ROUTES_BY_ID} from "../constants/back";

export default function DemoEcTransport() {
    const [productId, setProductId] = useState(''); // ID du produit entré par l'utilisateur
    const [processRoutes, setProcessRoutes] = useState([]); // Liste des trajets effectué durant le process du produit
    const [error, setError] = useState(null);

    const getProcessRoutes = () => {
        if (!productId) {
            alert("Veuillez entrer un ID produit valide.");
            return;
        }
        axios.get(`${GET_PROCESS_ROUTES_BY_ID}${productId}`)
            .then(response => {
                setProcessRoutes(response.data);
                setError(null);
            })
            .catch(error => {
                console.error("Erreur lors de la récupération des trajets :", error);
                console.error(error)
                setError("Une erreur s'est produite lors de la récupération des trajets. Veuillez vérifier l'ID produit ou réessayer.");
                setProcessRoutes([]);
            });
    };
    const setProcessRouteData = async () => {
        axios.get(GET_PROCESS_ROUTES)
            .then(response => {
                // Afficher toute la réponse du serveur dans la console
                console.log("Réponse complète du serveur : ", response);

                // Si vous souhaitez uniquement afficher les données :
                console.log("Données renvoyées par le serveur : ", response.data);

                // Si vous voulez vérifier le statut de la réponse (code HTTP) :
                console.log("Code de statut HTTP : ", response.status);

                // Ensuite, vous pouvez procéder avec votre traitement des données
                setProcessRoutes(response.data);
                setError(null);
            })
            .catch(error => {
                console.error("Erreur lors de la récupération des trajets :", error);
                setError("Une erreur s'est produite lors de la récupération des trajets.");
            });
    }




    return (
        <div className="container">
            <h2>Demo EC Transport</h2>
            <div>
                <label htmlFor="productIdInput">Entrer l'ID produit :</label>
                <input
                    id="productIdInput"
                    type="number"
                    value={productId}
                    onChange={(e) => setProductId(e.target.value)}
                    placeholder="ID Produit"
                />
                <button onClick={getProcessRoutes}>Rechercher</button>
            </div>
            <br />
            <button onClick={setProcessRouteData}>Tout afficher</button>
            <br />
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {processRoutes.length > 0 ? (
                <table className="table table-bordered table-hover">
                    <thead>
                    <tr>
                        <th>ID Route</th>
                        <th>Nom Route</th>
                        <th>Description</th>
                    </tr>
                    </thead>
                    <tbody>
                    {processRoutes.map((route) => (
                        <tr key={route.id}>
                            <td>{route.id}</td>
                            <td>{route.name}</td>
                            <td>{route.description}</td>

                        </tr>
                    ))}
                    </tbody>
                </table>
            ) : (
                !error && <p>Aucune route trouvée pour cet ID produit.</p>
            )}
        </div>
    );
}
