import React, { useState } from 'react';
import axios from 'axios';
import { GET_PROCESS_STEPS_BY_ID } from '../constants/back';

export default function DemoEcTransport() {
    const [productId, setProductId] = useState(''); // ID du produit entré par l'utilisateur
    const [processSteps, setProcessSteps] = useState([]); // Liste des étapes associées au produit
    const [error, setError] = useState(null);

    // Fonction pour récupérer les étapes du processus à partir de l'ID produit
    const fetchProcessSteps = async () => {
        if (!productId) {
            alert("Veuillez entrer un ID produit valide.");
            return;
        }

        try {
            const response = await axios.get(`${GET_PROCESS_STEPS_BY_ID}?productId=${productId}`);
            setProcessSteps(response.data);
            setError(null);
        } catch (err) {
            console.error("Erreur lors de la récupération des étapes de processus :", err);
            setError("Une erreur s'est produite lors de la récupération des étapes. Veuillez vérifier l'ID produit ou réessayer.");
            setProcessSteps([]);
        }
    };

    return (
        <div className="container">
            <h2>Demo EC Transport</h2>
            <div>
                <label htmlFor="productIdInput">Entrer l'ID produit :</label>
                <input
                    id="productIdInput"
                    type="number"
                    value={productId}
                    onChange={(e) => setProductId(e.target.value)}
                    placeholder="ID Produit"
                />
                <button onClick={fetchProcessSteps}>Rechercher</button>
            </div>
            <br />
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {processSteps.length > 0 ? (
                <table className="table table-bordered table-hover">
                    <thead>
                    <tr>
                        <th>ID Étape</th>
                        <th>Nom Étape</th>
                        <th>Description</th>
                    </tr>
                    </thead>
                    <tbody>
                    {processSteps.map((step) => (
                        <tr key={step.id}>
                            <td>{step.id}</td>
                            <td>{step.name}</td>
                            <td>{step.description}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            ) : (
                !error && <p>Aucune étape trouvée pour cet ID produit.</p>
            )}
        </div>
    );
}
