import React, { useEffect, useState } from 'react';
import axios from "axios";
import '../styles/Sample.css';
import { GET_SAMPLES, LOCAL_HOST_SAMPLE, UPDATE_SAMPLES, ADD_SAMPLES } from "../constants/back";

export default function Sample() {
  const [samples, setSamples] = useState([]);
  const [newSample, setNewSample] = useState({
    dateSample: '',
    stringSample: '',
    floatSample: '',
    sampleType: 'SAMPLE_TYPE1' // Exemple de valeur par défaut
  });

  const [editingSample, setEditingSample] = useState(null); // Ajout de l'état pour l'édition

  const setSampleData = async () => {
    axios.get(GET_SAMPLES).then((response) => {
      setSamples(response.data);
    }).catch(error => {
      alert("Error occurred while loading data: " + error);
    });
  };

  const addSample = async () => {
    axios.post(ADD_SAMPLES, newSample).then(() => {
      setSampleData();
      setNewSample({
        dateSample: '',
        stringSample: '',
        floatSample: '',
        sampleType: 'SAMPLE_TYPE1'
      }); // Réinitialiser le formulaire après l'ajout
    }).catch(error => {
      alert("Error occurred while adding sample: " + error);
    });
  };

  const updateSample = async (id) => {
    axios.put(UPDATE_SAMPLES + '/' + id, editingSample).then(() => {
      setSampleData();
      setEditingSample(null); // Réinitialiser l'état après la mise à jour
    }).catch(error => {
      alert("Error occurred while updating sample: " + error);
    });
  };

  const deleteSample = async (id) => {
    axios.delete(LOCAL_HOST_SAMPLE + id).then(() => {
      setSampleData();
    }).catch(error => {
      alert("Error occurred while deleting sample: " + error);
    });
  };

  const startEditing = (sample) => {
    setEditingSample({ ...sample }); // Initialiser l'échantillon en cours d'édition
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (editingSample) {
      setEditingSample({ ...editingSample, [name]: value });
    } else {
      setNewSample({ ...newSample, [name]: value });
    }
  };

  useEffect(() => {
    setSampleData();
  }, []);

  return (
    <div className="container text-center">
      <h4 className="mx-2">Samples List</h4>
      <div className="row">
        <table className="table table-sm table-bordered table-hover">
          <thead>
            <tr>
              <th scope="col">Id</th>
              <th scope="col">Date</th>
              <th scope="col">String</th>
              <th scope="col">Float</th>
              <th scope="col">Type</th>
              <th scope="col">Actions</th>
            </tr>
          </thead>
          <tbody className="table-group-divider">
            {samples.map((sample, index) => (
              <tr key={index}>
                <th scope="row">{sample.idSample}</th>
                <td>{editingSample?.idSample === sample.idSample ?
                  <input type="date" className="form-control" name="dateSample" value={editingSample.dateSample} onChange={handleChange} /> :
                  sample.dateSample}</td>
                <td>{editingSample?.idSample === sample.idSample ?
                  <input type="text" className="form-control" name="stringSample" value={editingSample.stringSample} onChange={handleChange} /> :
                  sample.stringSample}</td>
                <td>{editingSample?.idSample === sample.idSample ?
                  <input type="number" className="form-control" name="floatSample" value={editingSample.floatSample} onChange={handleChange} /> :
                  sample.floatSample}</td>
                <td>{editingSample?.idSample === sample.idSample ?
                  <select className="form-control" name="sampleType" value={editingSample.sampleType} onChange={handleChange}>
                    <option value="SAMPLE_TYPE1">SAMPLE_TYPE1</option>
                    <option value="SAMPLE_TYPE2">SAMPLE_TYPE2</option>
                  </select> :
                  sample.sampleType}</td>
                <td>
                  {editingSample?.idSample === sample.idSample ?
                    <>
                      <button className="btn btn-outline-success" onClick={() => updateSample(sample.idSample)}>Update</button>
                      <button className="btn btn-outline-secondary" onClick={() => setEditingSample(null)}>Cancel</button>
                    </> :
                    <>
                      <button className="btn btn-outline-primary" onClick={() => startEditing(sample)}>Edit</button>
                      <button className="btn btn-outline-danger" onClick={() => deleteSample(sample.idSample)}>Delete</button>
                    </>
                  }
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Formulaire d'ajout d'un nouvel échantillon */}
      <div className="form-group">
        <button className="btn btn-outline-primary" data-bs-toggle="modal" data-bs-target="#addSampleModal">
          Add a Sample
        </button>
      </div>

      {/* Modal pour ajouter un nouvel échantillon */}
      <div className="modal fade" id="addSampleModal" tabIndex="-1" aria-labelledby="addSampleModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="addSampleModalLabel">Add Sample</h5>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
              <input type="date" className="form-control mb-2" name="dateSample" value={newSample.dateSample} onChange={handleChange} />
              <input type="text" className="form-control mb-2" name="stringSample" value={newSample.stringSample} onChange={handleChange} />
              <input type="number" className="form-control mb-2" name="floatSample" value={newSample.floatSample} onChange={handleChange} />
              <select className="form-control mb-2" name="sampleType" value={newSample.sampleType} onChange={handleChange}>
                <option value="SAMPLE_TYPE1">SAMPLE_TYPE1</option>
                <option value="SAMPLE_TYPE2">SAMPLE_TYPE2</option>
              </select>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
              <button type="button" className="btn btn-primary" onClick={addSample} data-bs-dismiss="modal">Add Sample</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
