import React, { useEffect, useState } from 'react';
import axios from "axios";
import '../styles/Sample.css';
import { GET_SAMPLES, LOCAL_HOST_SAMPLE, UPDATE_SAMPLES, ADD_SAMPLES } from "../constants/back";

export default function Sample() {
  const [samples, setSamples] = useState([]);
  const [newSample, setNewSample] = useState({
    dateSample: '',
    stringSample: '',
    floatSample: '',
    sampleType: 'SAMPLE_TYPE1' // Exemple de valeur par défaut
  });
  const [isAdding, setIsAdding] = useState(false);
  const [editingSample, setEditingSample] = useState(null); // Nouvel état pour l'échantillon en cours d'édition

  // Fonction pour charger les échantillons
  const setSampleData = async () => {
    axios.get(GET_SAMPLES).then((response) => {
      setSamples(response.data);
    }).catch(error => {
      alert("Error occurred while loading data: " + error);
    });
  };

  // Fonction pour ajouter un échantillon
  const addSample = async () => {
    axios.post(ADD_SAMPLES, newSample).then(() => {
      setSampleData(); // Récupérer les échantillons après ajout
      setIsAdding(false); // Cacher le formulaire après l'ajout
    }).catch(error => {
      alert("Error occurred while adding sample: " + error);
    });
  };

  // Fonction pour mettre à jour un échantillon
  const updateSample = async (id) => {
    axios.put(UPDATE_SAMPLES + '/' + id, editingSample).then(() => {
      setSampleData(); // Récupérer les échantillons après mise à jour
      setEditingSample(null); // Réinitialiser l'échantillon en cours d'édition
    }).catch(error => {
      alert("Error occurred while updating sample: " + error);
    });
  };

  // Fonction pour afficher le formulaire d'édition
  const startEditing = (sample) => {
    setEditingSample({ ...sample });
  };

  // Fonction pour annuler l'édition
  const cancelEditing = () => {
    setEditingSample(null);
  };

  // Effet pour charger les données au montage
  useEffect(() => {
    setSampleData();
  }, []);

  // Formulaire d'ajout d'échantillon
  const handleChangeNewSample = (e) => {
    const { name, value } = e.target;
    setNewSample({ ...newSample, [name]: value });
  };

  // Formulaire d'édition d'échantillon
  const handleChangeEditingSample = (e) => {
    const { name, value } = e.target;
    setEditingSample({ ...editingSample, [name]: value });
  };

  if (samples.length === 0)
    return (<div className="container text-center">No samples</div>);

  return (
    <div className="container text-center">
      <h4 className="mx-2">Samples List</h4>
      <div className="row">
        <table className="table table-sm table-bordered table-hover">
          <thead>
            <tr>
              <th scope="col">Id</th>
              <th scope="col">Date</th>
              <th scope="col">String</th>
              <th scope="col">Float</th>
              <th scope="col">Type</th>
              <th scope="col">Actions</th>
            </tr>
          </thead>
          <tbody className="table-group-divider">
            {samples.map((sample, index) => (
              <tr key={index}>
                <th scope="row">{sample.idSample}</th>
                <td>{editingSample?.idSample === sample.idSample ? (
                  <input type="date" className="form-control" name="dateSample" value={editingSample.dateSample} onChange={handleChangeEditingSample} />
                ) : sample.dateSample}</td>
                <td>{editingSample?.idSample === sample.idSample ? (
                  <input type="text" className="form-control" name="stringSample" value={editingSample.stringSample} onChange={handleChangeEditingSample} />
                ) : sample.stringSample}</td>
                <td>{editingSample?.idSample === sample.idSample ? (
                  <input type="number" className="form-control" name="floatSample" value={editingSample.floatSample} onChange={handleChangeEditingSample} />
                ) : sample.floatSample}</td>
                <td>{editingSample?.idSample === sample.idSample ? (
                  <select className="form-control" name="sampleType" value={editingSample.sampleType} onChange={handleChangeEditingSample}>
                    <option value="SAMPLE_TYPE1">SAMPLE_TYPE1</option>
                    <option value="SAMPLE_TYPE2">SAMPLE_TYPE2</option>
                  </select>
                ) : sample.sampleType}</td>
                <td>
                  {editingSample?.idSample === sample.idSample ? (
                    <>
                      <button className="btn btn-outline-success" onClick={() => updateSample(sample.idSample)}>Save</button>
                      <button className="btn btn-outline-secondary" onClick={cancelEditing}>Cancel</button>
                    </>
                  ) : (
                    <>
                      <button className="btn btn-outline-primary" onClick={() => startEditing(sample)}>Edit</button>
                      <button className="btn btn-outline-danger" onClick={() => confirmRemoveSample(sample.idSample)}>Delete</button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Formulaire d'ajout d'un nouvel échantillon */}
      {isAdding && (
        <div className="form-group">
          <input type="date" className="form-control" name="dateSample" value={newSample.dateSample} onChange={handleChangeNewSample} />
          <input type="text" className="form-control" name="stringSample" value={newSample.stringSample} onChange={handleChangeNewSample} />
          <input type="number" className="form-control" name="floatSample" value={newSample.floatSample} onChange={handleChangeNewSample} />
          <select className="form-control" name="sampleType" value={newSample.sampleType} onChange={handleChangeNewSample}>
            <option value="SAMPLE_TYPE1">SAMPLE_TYPE1</option>
            <option value="SAMPLE_TYPE2">SAMPLE_TYPE2</option>
          </select>
          <button className="btn btn-outline-primary" onClick={addSample}>Add Sample</button>
          <button className="btn btn-outline-secondary" onClick={() => setIsAdding(false)}>Cancel</button>
        </div>
      )}

      {!isAdding && (
        <button className="btn btn-outline-success" onClick={() => setIsAdding(true)}>Add a Sample</button>
      )}
    </div>
  );
}
