import React, { useState } from 'react';

export default function Search() {
    const [searchTerm, setSearchTerm] = useState(''); // pour stocker les mots-clés
    const [results, setResults] = useState([]); // Stocker les résultats du backend
    const [loading, setLoading] = useState(false); // Indicateur de chargement

    const handleSearch = async () => {
        console.log("Début de la recherche");
        console.log("Mots-clés recherchés : ", searchTerm);
        setLoading(true);

        try {
            // Appel au backend avec les mots-clés
            const response = await fetch('/search', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    keywords: searchTerm.trim(), // Envoi des mots-clés au backend
                }),
            });

            console.log("Réponse reçue : ", response);

            if (!response.ok) {
                throw new Error(`Erreur HTTP : ${response.status} - ${response.statusText}`);
            }

            const data = await response.json();
            console.log("Données reçues : ", data);
            setResults(data); // Stocke les résultats retournés par le backend
        } catch (error) {
            console.error("Erreur lors de la recherche :", error);
            alert(`Une erreur est survenue : ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSearch();
        }
    };

    return (
        <div className="container text-center">
            <h1>Recherche</h1>
            <div className="search-bar">
                <input
                    type="text"
                    placeholder="Que cherchez-vous ?"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    onKeyDown={handleKeyPress} // Détection de la touche "Enter"
                />
                <button onClick={handleSearch} disabled={loading}>
                    {loading ? "Recherche en cours..." : "Rechercher"}
                </button>
            </div>

            <div className="results">
                {results.length > 0 && (
                    <ul>
                        {results.map((result, index) => (
                            <li key={index}>{result.name}</li> // Adaptez selon les données renvoyées
                        ))}
                    </ul>
                )}
                {results.length === 0 && !loading && <p>Aucun résultat trouvé.</p>}
            </div>
        </div>
    );
}
